  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("p", null, "BERT \uC800\uC790\uC9C4\uC740 \uB450 \uBB38\uC7A5 \uAC04 \uC720\uC0AC\uB3C4\uB97C \uACC4\uC0B0\uD558\uB294 Semantic Textual Similarity, \uB450 \uBB38\uC7A5 \uC0AC\uC774 \uCD94\uB860 \uAD00\uACC4\uB97C \uBD84\uB958\uD558\uB294 Natural Language Inference \uB4F1\uACFC \uAC19\uC774 \uB450 \uBB38\uC7A5\uC744 \uC785\uB825\uC73C\uB85C \uBC1B\uC544 \uD558\uB098\uC758 \uAC12\uC744 \uBC18\uD658\uD558\uB294 \uBB38\uC81C \uC138\uD305\uC5D0\uC11C ", "[SEP]", " \uD1A0\uD070\uC744 \uD65C\uC6A9\uD55C Cross-encoder \uBC29\uC2DD\uC744 \uCC44\uD0DD\uD558\uC600\uC2B5\uB2C8\uB2E4."), mdx("p", null, "\uD2B8\uB79C\uC2A4\uD3EC\uBA38 \uC544\uD0A4\uD14D\uCC98\uB97C \uD65C\uC6A9\uD55C Cross-encoder \uBC29\uC2DD\uC758 \uC7A5\uC810\uC740 \uB450 \uBB38\uC7A5\uC774 Self-attention\uC744 \uD1B5\uD574 \uC0C1\uD638 \uAC04 attend \uB420 \uC218 \uC788\uC73C\uBBC0\uB85C \uB450 \uBB38\uC7A5 \uC0AC\uC774\uC758 \uAD00\uACC4\uB97C \uBA74\uBC00\uD558\uAC8C \uD30C\uC545\uD560 \uC218 \uC788\uB2E4\uB294 \uC810\uC5D0 \uC788\uC2B5\uB2C8\uB2E4. \uB450 \uBB38\uC7A5\uC744 \uAD6C\uC131\uD558\uB294 \uBAA8\uB4E0 \uD1A0\uD070\uB4E4 \uAC04 \uC5B4\uD150\uC158 \uC5F0\uC0B0\uC774 \uC218\uD589\uB418\uAE30 \uB54C\uBB38\uC774\uC8E0."), mdx("p", null, "\uADF8\uB7EC\uB098 Cross-encoder\uB294 \uCE58\uBA85\uC801\uC778 \uB2E8\uC810\uC744 \uC9C0\uB2C8\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uBC14\uB85C \uBB38\uC7A5 \uAC04 \uC720\uC0AC\uB3C4\uB97C \uD65C\uC6A9\uD55C \uAC80\uC0C9, \uC784\uBCA0\uB529\uC744 \uD65C\uC6A9\uD55C \uD074\uB7EC\uC2A4\uD130\uB9C1 \uB4F1\uC5D0 \uC788\uC5B4 \uC5F0\uC0B0\uB7C9\uC758 \uD55C\uACC4\uAC00 \uC874\uC7AC\uD55C\uB2E4\uB294 \uC810\uC785\uB2C8\uB2E4. \uAC80\uC0C9 \uC5D4\uC9C4\uC5D0 10,000\uAC1C \uBB38\uC11C\uB97C \uB300\uC0C1\uC73C\uB85C \uD55C \uC11C\uCE58 \uCFFC\uB9AC\uB97C \uBCF4\uB0B8\uB2E4\uACE0 \uC0DD\uAC01\uD574\uBD05\uC2DC\uB2E4. \uC11C\uCE58 \uCFFC\uB9AC\uC640 \uC720\uC0AC\uD55C Top-k \uBB38\uC11C\uB97C \uCC3E\uAE30 \uC704\uD574 \uC6B0\uB9AC\uB294 \u201D", "[CLS]", " \uC11C\uCE58 \uCFFC\uB9AC ", "[SEP]", " n-th \uBB38\uC11C ", "[SEP]", "\u201C\uC758 \uC784\uBCA0\uB529\uC744 10,000 \uBC88\uC758 "), mdx("p", null, "Cross-encoder \uC5F0\uC0B0\uC744 \uD1B5\uD574 \uAD6C\uD574\uC57C\uD569\uB2C8\uB2E4. \uC785\uB825 \uBC1B\uC740 \uC11C\uCE58 \uCFFC\uB9AC\uC5D0 \uB530\uB77C \uC2DC\uD000\uC2A4 \uC784\uBCA0\uB529\uC774 \uBCC0\uD558\uAE30\uC5D0 \uB9E4\uBC88 \uC0C8\uB85C\uC6B4 \uC5F0\uC0B0\uC744 \uC218\uD589\uD574\uC57C \uD558\uB294 \uAC83\uC774\uC8E0.\n\uC774\uB7EC\uD55C \uC5F0\uC0B0\uB7C9\uC758 \uD55C\uACC4\uB97C \uC9C0\uC801\uD558\uBA70 \uB4F1\uC7A5\uD55C \uB17C\uBB38\uC774 2019\uB144 UKP Lab\uC5D0\uC11C \uB0B4\uB193\uC558\uB358 Sentence BERT (SBERT) \uC785\uB2C8\uB2E4. SBERT\uB294 Siamese Network\uB97C \uB530\uB77C \uBAA8\uB4E0 \uBB38\uC7A5\uC744 Cross-encoder \uD615\uC2DD\uC774 \uC544\uB2CC \uB2E8\uC77C \uC778\uCF54\uB354 \uD615\uC2DD\uC73C\uB85C \uC5F0\uC0B0\uD574 \uAC01 \uBB38\uC7A5\uC758 \uC784\uBCA0\uB529\uC744 \uAE30\uC900\uC73C\uB85C \uC720\uC0AC\uB3C4\uB97C \uAD6C\uD558\uB294 \uC544\uD0A4\uD14D\uCC98\uB97C \uC81C\uC2DC\uD558\uC600\uC2B5\uB2C8\uB2E4. ", "[\uADF8\uB9BC 1]"), mdx("p", null, "\uC774\uB807\uAC8C \uB418\uBA74 \uC55E\uC11C \uC5B8\uAE09\uD55C \uC5F0\uC0B0\uB7C9 \uBB38\uC81C\uAC00 \uC5B4\uB5BB\uAC8C \uD574\uACB0\uB420 \uC218 \uC788\uC744\uAE4C\uC694? \uC6B0\uC120 \uAC80\uC0C9 \uB300\uC0C1\uC774 \uB418\uB294 10,000 \uAC1C\uC758 \uBB38\uC11C\uB294 SBERT\uC5D0 \uC758\uD574 Pre-computed \uB418\uC5B4 \uC800\uC7A5\uB420 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4. Cross-encoder \uBC29\uC2DD\uC5D0\uC11C\uB294 \uB9E4\uBC88 \uB2EC\uB77C\uC9C0\uB294 \uC11C\uCE58 \uCFFC\uB9AC\uC640 \uC0C8\uB85C\uC6B4 \uC5F0\uC0B0\uC744 \uD1B5\uD574 \uC720\uC0AC\uB3C4\uB97C \uAD6C\uD574\uC57C\uD558\uBBC0\uB85C \uBBF8\uB9AC \uACC4\uC0B0\uC744 \uD574\uB193\uC744 \uC218 \uC5C6\uC9C0\uB9CC, SBERT \uBC29\uC2DD\uC5D0\uC11C\uB294 \uAC01 \uBB38\uC7A5\uC758 \uC784\uBCA0\uB529 \uC5F0\uC0B0\uC774 \uB3C5\uB9BD\uC801\uC73C\uB85C \uC218\uD589\uB418\uACE0 \uC774\uB807\uAC8C \uD574\uC11C \uC5BB\uC5B4\uC9C4 \uC784\uBCA0\uB529\uC744 \uAE30\uC900\uC73C\uB85C \uC720\uC0AC\uB3C4\uB9CC \uAD6C\uD558\uBA74 \uB418\uAE30 \uB54C\uBB38\uC785\uB2C8\uB2E4."), mdx("p", null, "\uC989, \uAC80\uC0C9 \uD6C4\uBCF4\uAC00 \uB418\uB294 \uBB38\uC11C\uB4E4\uC5D0 \uB300\uD574\uC11C\uB294 \uBBF8\uB9AC \uC784\uBCA0\uB529 \uACC4\uC0B0\uC744 \uD574\uB193\uC740 \uD6C4, \uC0C8\uB85C\uC774 \uB4E4\uC5B4\uC628 \uC11C\uCE58 \uCFFC\uB9AC\uC758 \uC784\uBCA0\uB529\uB9CC \uC5BB\uC740 \uD6C4 \uBB38\uC11C\uB4E4\uC758 \uC784\uBCA0\uB529\uACFC \uC720\uC0AC\uB3C4\uB9CC \uAD6C\uD558\uBA74 \uB418\uB294 \uBB38\uC81C\uB85C \uCE58\uD658\uD560 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4. \uC774\uB807\uAC8C \uB418\uBA74 \uB9CE\uC740 \uC5F0\uC0B0\uB7C9\uC774 \uD544\uC694\uD55C BERT \uC784\uBCA0\uB529\uC744 10,000 \uBC88\uC5D0\uC11C 1\uBC88\uC73C\uB85C \uC904\uC77C \uC218 \uC788\uC744 \uBFD0\uB354\uB7EC, FAISS\uC640 \uAC19\uC740 Approximate Nearest Neighbor Search \uD328\uD0A4\uC9C0\uB97C \uD65C\uC6A9\uD574 \uC720\uC0AC\uB3C4\uB97C \uAD6C\uD558\uB294 \uC5F0\uC0B0\uB3C4 \uC801\uC740 \uCEF4\uD4E8\uD305\uC73C\uB85C \uC218\uD589\uD560 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4."), mdx("p", null, "SBERT\uC5D0\uC11C \uCC28\uC6A9\uD55C \uD574\uB2F9 \uAD6C\uC870\uB97C Bi-encoder\uB77C\uACE0 \uC774\uC57C\uAE30\uD569\uB2C8\uB2E4. \uC608\uC81C\uC5D0\uC11C \uB9D0\uC500\uB4DC\uB9B0 \uBC14\uC640 \uAC19\uC774 Computation-heavy \uD55C Cross-encoder \uAD6C\uC870\uB294 \uC0C1\uC6A9\uB2E8\uC5D0\uC11C \uD65C\uC6A9\uC774 \uBD88\uAC00\uB2A5\uD558\uAE30\uC5D0, Bi-encoder\uAC00 \uD604\uC2E4\uC801 \uB300\uC548\uC778 \uAC83\uC740 \uC0AC\uC2E4\uC785\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uB9CE\uC740 \uC5F0\uAD6C\uC5D0\uC11C Bi-encoder\uC758 \uD0DC\uC2A4\uD06C \uC131\uB2A5\uC774 Cross-encoder\uC758 \uC131\uB2A5\uBCF4\uB2E4 \uB5A8\uC5B4\uC9D0\uC744 \uC2E4\uD5D8\uC744 \uD1B5\uD574 \uBCF4\uC5EC\uC8FC\uC5C8\uC2B5\uB2C8\uB2E4. \uC804\uC0B0\uD559\uC5D0\uC11C\uC758 \uC131\uB2A5 vs. \uC18D\uB3C4\uC758 \uD2B8\uB808\uC774\uB4DC \uC624\uD504\uAC00 \uC774\uACF3\uC5D0\uC11C\uB3C4 \uBC1C\uC0DD\uD558\uB294 \uAC83\uC774\uC8E0 !"), mdx("p", null, "\uD2B9\uD788 \uC774\uB7EC\uD55C \uC131\uB2A5 \uCC28\uC774\uB294 Fine-tuning\uC744 \uD560 \uC218 \uC788\uB294 \uB370\uC774\uD130\uC14B\uC774 \uBD80\uC871\uD560 \uB54C \uB354 \uC2EC\uD558\uAC8C \uB098\uD0C0\uB098\uAC8C \uB418\uB294\uB370 STS, NLI \uB4F1\uC758 \uD398\uC5B4 \uB370\uC774\uD130\uC14B\uC740 \uC5B4\uB178\uD14C\uC774\uC158\uC744 \uD558\uB294\uB370 \uB9CE\uC740 \uBE44\uC6A9\uC774 \uB4E4\uAE30 \uB54C\uBB38\uC5D0 \uD604\uC2E4\uC801\uC73C\uB85C \uC27D\uAC8C \uC218\uAE09\uD560 \uC218\uB3C4 \uC5C6\uB2E4\uB294\uB370\uC5D0\uC11C \uAC1C\uC120\uC758 \uD55C\uACC4\uAC00 \uB098\uD0C0\uB098\uAC8C \uB429\uB2C8\uB2E4. ", "[\uADF8\uB9BC 2]"), mdx("p", null, "\uD574\uB2F9 \uBB38\uC81C\uB97C \uB204\uAD6C\uBCF4\uB2E4 \uC798 \uC54C\uACE0 \uC788\uC5C8\uC744 \uC0AC\uB78C\uB4E4\uC740 \uB2F9\uC5F0\uD788 SBERT\uB97C \uACE0\uC548\uD55C \uB7A9\uBD84\uB4E4\uC774\uACA0\uC8E0? \uC11C\uB860\uC774 \uAE38\uC5C8\uC2B5\uB2C8\uB2E4 ! \uC774\uBC88 NAACL 2021\uC5D0 \uC5B5\uC149\uB41C Augmented SBERT\uB294 \uC55E\uC11C \uC5B8\uAE09\uD55C \uBB38\uC81C\uB85C \uC778\uD574 \uD604\uC2E4\uC801\uC73C\uB85C \uCC28\uC6A9\uD558\uACE0 \uC788\uB294 Bi-encoder \uAD6C\uC870\uC758 SBERT \uC131\uB2A5 \uC774\uC288\uB97C \uAC1C\uC120\uD558\uAE30 \uC704\uD574 Un-labeled \uB370\uC774\uD130\uC14B\uC5D0\uC11C Silver \uB370\uC774\uD130\uC14B\uC744 \uCD94\uCD9C\uD574 Gold \uB370\uC774\uD130\uC14B\uACFC \uD63C\uD569\uD558\uC5EC Fine-tuning \uD558\uC600\uC744 \uB54C, \uC131\uB2A5 \uAC1C\uC120\uC744 \uB3C4\uBAA8\uD560 \uC218 \uC788\uB2E4\uB294 \uC8FC\uC7A5\uC744 \uC81C\uAE30\uD55C \uB17C\uBB38\uC785\uB2C8\uB2E4."), mdx("p", null, "\uAC00\uC7A5 \uC911\uC694\uD558\uAC8C \uB2E4\uB8E8\uC5B4\uC838\uC57C \uD560 Un-labeled \uB370\uC774\uD130\uC14B\uC5D0\uC11C Silver \uB370\uC774\uD130\uC14B\uC744 \uCD94\uCD9C\uD558\uB294 \uAE30\uBC95\uC5D0 \uB300\uD574 \uC0B4\uD3B4\uBD05\uC2DC\uB2E4. Un-labeled \uB370\uC774\uD130\uB97C Silver \uB370\uC774\uD130\uB85C \uB77C\uBCA8\uB9C1\uD558\uB294\uB370 \uD65C\uC6A9\uB418\uB294 \uBAA8\uB4C8\uC740 Fine-tuning \uB41C BERT (Not SBERT) \uC785\uB2C8\uB2E4. ", "[\uADF8\uB9BC 3]", "\uC744 \uBCF4\uC2DC\uBA74 Unlabeled \uB370\uC774\uD130\uAC00 Cross-encoder\uB97C \uAC70\uCCD0 Silver \uB370\uC774\uD130\uAC00 \uB418\uB294 \uAC83\uC744 \uD655\uC778\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4."), mdx("p", null, "\uC989, Un-labeled \uD398\uC5B4 \uB370\uC774\uD130\uB97C \uC131\uB2A5\uC774 \uC88B\uC740 Cross-encoder \uD615\uD0DC\uC758 BERT\uB85C Weakly-labeled \uB370\uC774\uD130\uB85C \uBCC0\uD658 \uD6C4, \uC774\uB97C Gold \uB370\uC774\uD130\uC14B\uC5D0 \uC99D\uAC15\uD574 \uD6C8\uB828\uC5D0 \uD65C\uC6A9\uD558\uB294 \uAD6C\uC870\uB77C\uACE0 \uC774\uD574\uD558\uC2DC\uBA74 \uB429\uB2C8\uB2E4. \uAD49\uC7A5\uD788 \uAC04\uB2E8\uD558\uC9C0\uB9CC \uC2E4\uD5D8 \uBC30\uACBD\uACFC \uAC00\uC815\uC5D0 \uC758\uAC70\uD558\uBA74 \uC124\uB4DD\uB825\uC774 \uC788\uB294 \uC138\uD305\uC785\uB2C8\uB2E4."), mdx("p", null, "\uCD94\uCD9C\uC758 \uB300\uC0C1\uC774 \uB418\uB294 Un-labeled \uB370\uC774\uD130\uC14B\uC740 \uC784\uC758\uC758 \uBB38\uC7A5 \uB450 \uC30D\uC73C\uB85C \uAD6C\uC131\uB41C \uC544\uBB34 \uB370\uC774\uD130\uC14B\uC73C\uB85C \uC124\uC815\uD560 \uC218\uB3C4 \uC788\uC9C0\uB9CC, \uC774\uBBF8 \uC5B4\uB178\uD14C\uC774\uC158 \uB41C \uB370\uC774\uD130\uC14B\uC5D0\uC11C \uC5B4\uB178\uD14C\uC774\uC158 \uB418\uC9C0 \uC54A\uC740 \uC870\uD569\uC744 \uCD94\uCD9C\uD558\uC5EC \uAD6C\uC131\uB41C \uB370\uC774\uD130\uC14B \uC5ED\uC2DC Un-labeled \uB370\uC774\uD130\uC14B\uC73C\uB85C \uC124\uC815\uD560 \uC218 \uC788\uC744 \uAC83\uC785\uB2C8\uB2E4 (1\uBC88\uC758 A \uBB38\uC7A5\uACFC 2\uBC88\uC758 B \uBB38\uC7A5 \uAC04 \uB77C\uBCA8\uB9C1\uC740 \uB418\uC5B4 \uC788\uC9C0 \uC54A\uC73C\uBBC0\uB85C). \uB17C\uBB38\uC5D0\uC11C\uB294 \uD6C4\uC790\uC758 \uBC29\uBC95\uC744 \uCC44\uD0DD\uD569\uB2C8\uB2E4."), mdx("p", null, "\uC774\uB807\uAC8C \uB418\uBA74 \uC804\uCCB4 n \uAC1C \uD6C8\uB828 \uB370\uC774\uD130 \uC911 n x (n - 1) / 2